package com.iyam.mycash.data.network.api.serviceimport com.iyam.mycash.data.local.datastore.UserPreferenceDataSourceimport kotlinx.coroutines.flow.firstimport kotlinx.coroutines.runBlockingimport okhttp3.Interceptorimport okhttp3.Responseclass AuthorizationInterceptor(    private val userPref: UserPreferenceDataSource) : Interceptor {    override fun intercept(chain: Interceptor.Chain): Response {        val authToken = runBlocking {            userPref.getUserTokenFlow().first()        }        val request = chain.request()        val authenticatedRequest = if (authToken.isNotEmpty()) {            request.newBuilder()                .header("Authorization", "Bearer $authToken")                .build()        } else {            request        }        return chain.proceed(authenticatedRequest)    }}